PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE `OSP_MASTER` (`uid` `TEXT`, `oclass` `REAL`, `first_level` `BOOLEAN`, PRIMARY KEY(`uid`));
CREATE TABLE `OSP_RELATIONSHIPS` (`origin` `TEXT`, `target` `TEXT`, `name` `REAL`, `target_oclass` `REAL`, PRIMARY KEY(`origin`, `target`, `name`), FOREIGN KEY(`origin`) REFERENCES `OSP_MASTER`(`uid`), FOREIGN KEY(`target`) REFERENCES `OSP_MASTER`(`uid`));
CREATE TABLE `CUDS_city___City` (`name` `TEXT`, `coordinates___0` `INTEGER`, `coordinates___1` `INTEGER`, `uid` `TEXT`, PRIMARY KEY(`uid`), FOREIGN KEY(`uid`) REFERENCES `OSP_MASTER`(`uid`));
CREATE TABLE `CUDS_city___Citizen` (`age` `INTEGER`, `name` `TEXT`, `uid` `TEXT`, PRIMARY KEY(`uid`), FOREIGN KEY(`uid`) REFERENCES `OSP_MASTER`(`uid`));
CREATE TABLE `CUDS_city___Neighborhood` (`name` `TEXT`, `coordinates___0` `INTEGER`, `coordinates___1` `INTEGER`, `uid` `TEXT`, PRIMARY KEY(`uid`), FOREIGN KEY(`uid`) REFERENCES `OSP_MASTER`(`uid`));
CREATE TABLE `CUDS_city___Street` (`name` `TEXT`, `coordinates___0` `INTEGER`, `coordinates___1` `INTEGER`, `uid` `TEXT`, PRIMARY KEY(`uid`), FOREIGN KEY(`uid`) REFERENCES `OSP_MASTER`(`uid`));
CREATE TABLE `CUDS_city___Building` (`name` `TEXT`, `uid` `TEXT`, PRIMARY KEY(`uid`), FOREIGN KEY(`uid`) REFERENCES `OSP_MASTER`(`uid`));
CREATE TABLE `CUDS_city___Address` (`postalCode` `INTEGER`, `number` `INTEGER`, `name` `TEXT`, `uid` `TEXT`, PRIMARY KEY(`uid`), FOREIGN KEY(`uid`) REFERENCES `OSP_MASTER`(`uid`));
CREATE INDEX `idx_OSP_MASTER_oclass` ON `OSP_MASTER`(`oclass`);
CREATE INDEX `idx_OSP_MASTER_first_level` ON `OSP_MASTER`(`first_level`);
CREATE INDEX `idx_OSP_RELATIONSHIPS_origin` ON `OSP_RELATIONSHIPS`(`origin`);
COMMIT;
